fun sumOfEvenFibonacci(x: Int, y:Int, n: Int = 0){
    var firstNum = x
    var secondNum = y
    var sum = n

    if(y % 2 == 0)
        sum+=y

    var z = x + y
    firstNum = secondNum
    secondNum = z

    if(firstNum >= 4000000)
        println(sum)
    else
        sumOfEvenFibonacci(firstNum, secondNum, sum)
}